package com.mulit;

public class Square1to4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		/*
		 * 1234 
		 * 1234 
		 * 1234 
		 * 1234
		 */

		int n=4;
		for(int i=0;i<n;i++) {
			
			for(int j=1;j<=n;j++) {
				
				System.out.print(j);
				
			}
			
			System.out.println();
		}

	}

}
